import {DataSource, PrimitiveFormValue} from '../../data';
import {ElementType} from '../Elements';
import {FormField} from './FormField';

/**
 * Options or dataSource must be supplied
 */
export interface BaseSelectField extends FormField {
  display?: 'auto' | 'radio' | 'checkbox' | 'select';
  type: ElementType.Select | ElementType.MultiSelect;
  options?: SelectOption[];
  dataSource?: DataSource;
  triggerAction?: string;
  hint?: string;
}

export interface SelectField extends BaseSelectField {
  type: ElementType.Select;
  display?: 'auto' | 'radio' | 'select';
}

export interface MultiSelectField extends BaseSelectField {
  type: ElementType.MultiSelect;
  display?: 'auto' | 'checkbox' | 'select';
}

export interface SelectOption {
  text: string;
  value: PrimitiveFormValue;
}
